jQuery.fn.autotab=function(){jQuery(this).keyup(function(c){switch(c.keyCode){case 9:return false;case 16:return false;case 20:return false;default:var b=jQuery(this).attr("maxlength");var a=jQuery(this).val().length;if(a>=b){jQuery(this).next('input[type="text"], input[type="password"]').focus();}}});};
